import * as pulumi from "@pulumi/pulumi";
import * as argocd from "@three14/pulumi-argocd";

// Clusters Generator
const clustersSelector = new argocd.ApplicationSet("clusters_selector", {
    metadata: {
        name: "clusters-selector",
    },
    spec: {
        generators: [{
            clusters: [{
                selector: {
                    matchLabels: {
                        "argocd.argoproj.io/secret-type": "cluster",
                    },
                },
            }],
        }],
        template: {
            metadata: {
                name: "{{name}}-clusters-selector",
            },
            spec: {
                sources: [{
                    repoUrl: "https://github.com/argoproj/argocd-example-apps/",
                    targetRevision: "HEAD",
                    path: "guestbook",
                }],
                destination: {
                    server: "{{server}}",
                    namespace: "default",
                },
            },
        },
    },
});
// Cluster Decision Resource Generator
const clusterDecisionResource = new argocd.ApplicationSet("cluster_decision_resource", {
    metadata: {
        name: "cluster-decision-resource",
    },
    spec: {
        generators: [{
            clusterDecisionResources: [{
                configMapRef: "my-configmap",
                name: "quak",
            }],
        }],
        template: {
            metadata: {
                name: "{{name}}-guestbook",
            },
            spec: {
                sources: [{
                    repoUrl: "https://github.com/argoproj/argocd-example-apps/",
                    targetRevision: "HEAD",
                    path: "guestbook",
                }],
