// Pull Request Generator - GitHub
const prGithub = new argocd.ApplicationSet("pr_github", {
    metadata: {
        name: "pr-github",
    },
    spec: {
        generators: [{
            pullRequests: [{
                github: {
                    api: "https://git.example.com/",
                    owner: "myorg",
                    repo: "myrepository",
                    appSecretName: "github-app-repo-creds",
                    tokenRef: {
                        secretName: "github-token",
                        key: "token",
                    },
                    labels: ["preview"],
                },
            }],
        }],
        template: {
            metadata: {
                name: "myapp-{{branch}}-{{number}}",
            },
            spec: {
                project: "default",
                sources: [{
                    repoUrl: "https://github.com/myorg/myrepo.git",
                    path: "kubernetes/",
                    targetRevision: "{{head_sha}}",
                    helm: {
                        parameters: [{
                            name: "image.tag",
                            value: "pull-{{head_sha}}",
                        }],
                    },
                }],
                destination: {
                    server: "https://kubernetes.default.svc",
                    namespace: "default",
                },
            },
        },
    },
});
// SCM Provider Generator - GitHub
const scmGithub = new argocd.ApplicationSet("scm_github", {
    metadata: {
        name: "scm-github",
    },
    spec: {
        generators: [{
            scmProviders: [{
                github: {
                    appSecretName: "gh-app-repo-creds",
                    organization: "myorg",
                },
            }],
        }],
        template: {
            metadata: {
                name: "{{repository}}",
            },
            spec: {
                project: "default",
                sources: [{
                    repoUrl: "{{url}}",
                    path: "kubernetes/",
                    targetRevision: "{{branch}}",
                }],
                destination: {
                    server: "https://kubernetes.default.svc",
                    namespace: "default",
                },
            },
        },
    },
});
// Progressive Sync - Rolling Update
const progressiveSync = new argocd.ApplicationSet("progressive_sync", {
    metadata: {
        name: "progressive-sync",
    },
    spec: {
        generators: [{
            lists: [{
                elements: [
                    {
                        cluster: "engineering-dev",
                        url: "https://1.2.3.4",
                        env: "env-dev",
                    },
                    {
                        cluster: "engineering-qa",
                        url: "https://2.4.6.8",
                        env: "env-qa",
                    },
                    {
                        cluster: "engineering-prod",
                        url: "https://9.8.7.6/",
                        env: "env-prod",
                    },
                ],
            }],
        }],
        strategy: {
            type: "RollingSync",
            rollingSyncs: [{
                steps: [
                    {
                        matchExpressions: [{
                            key: "envLabel",
                            operator: "In",
                            values: ["env-dev"],
                        }],
                    },
                    {
                        matchExpressions: [{
                            key: "envLabel",
                            operator: "In",
                            values: ["env-qa"],
                        }],
                        maxUpdate: "0",
                    },
                    {
                        matchExpressions: [{
                            key: "envLabel",
                            operator: "In",
                            values: ["env-prod"],
                        }],
                        maxUpdate: "10%",
                    },
                ],
            }],
        },
        goTemplate: true,
        template: {
            metadata: {
                name: "{{.cluster}}-guestbook",
                labels: {
                    envLabel: "{{.env}}",
                },
            },
            spec: {
                project: "default",
                sources: [{
                    repoUrl: "https://github.com/infra-team/cluster-deployments.git",
                    path: "guestbook/{{.cluster}}",
                    targetRevision: "HEAD",
                }],
                destination: {
                    server: "{{.url}}",
                    namespace: "guestbook",
                },
            },
        },
    },
});
