          - lists:
              - elements:
                  - cluster: engineering-dev
                    url: https://1.2.3.4
                    env: env-dev
                  - cluster: engineering-qa
                    url: https://2.4.6.8
                    env: env-qa
                  - cluster: engineering-prod
                    url: https://9.8.7.6/
                    env: env-prod
        strategy:
          type: RollingSync
